                }
                if (price > 0) {
                    address pair = factoryV3.getPool(wNative, stableUsdToken, fee);
                    uint256 balance = IERC20(wNative).balanceOf(pair);
                    totalPrice += price * balance;
                    wNativeTotal += balance;
                }
            }
        }

        if (wNativeTotal == 0) {
            return (0, wNativeTotal);
        }
        price = totalPrice / wNativeTotal;
